import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from "react-router-dom";
import { getCountries} from  "../redux/actions";
import './LandingPage.css';
import Paginated from "../Paginated/Paginated";

const LandingPage = () => {
    const dispatch = useDispatch();
    
    const countries = useSelector(state => state.countries); // obtener la lista de países del estado de Redux
    const currentPage = useSelector(state => state.currentPage); // obtener la página actual del estado de Redux
    const countriesPerPage = useSelector(state => state.countriesPerPage); // obtener el número de países por página del estado de Redux

    const handlePagination = (pageNumber) => {
        dispatch(setCurrentPage(pageNumber)); // actualizar el número de página actual en el estado de Redux
        dispatch(getCountries(pageNumber, countriesPerPage)); // llamar a la acción getCountries con los nuevos parámetros de paginación
    }
   
    const handleClick = () => {
        dispatch(getCountries());
    }
   return (
        <div className="container">
            <h1 >Bienvenido a un espacio dedicado a todos los paises del Mundo</h1>
             <Link to='/home'>  
              <button onClick={handleClick}>Iniciar Aventura</button>
            </Link>
       <div>
       
            <Link to='/countries'>Ver todos los países</Link>
            <Paginated countriesPerPage={countriesPerPage} countries={countries.length}
        paginated={handlePagination} currentPage={currentPage} />
            </div>
        </div>
    ) 
};

export default LandingPage;
