import React, { useState } from 'react';

const Paginated = ({ countriesPerPage, countries, paginated}) => {
  const [currentPage, setCurrentPage] = useState(1);
  const [searchTerm, setSearchTerm] = useState('');
  const pageNumbers = [];
  
  // Filtrar los países basados en el término de búsqueda
  const filteredCountries = countries.filter(country => country.name.toLowerCase().includes(searchTerm.toLowerCase()));

  for (let i = 1; i <= Math.ceil(filteredCountries.length/countriesPerPage); i++) {
    pageNumbers.push(i);
  }

  const handlePageClick = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
    setCurrentPage(1); // Volver a la página 1 al realizar una búsqueda
  };

  const indexOfLastCountry = currentPage * countriesPerPage;
  const indexOfFirstCountry = indexOfLastCountry - countriesPerPage;
  const currentCountries = filteredCountries.slice(indexOfFirstCountry, indexOfLastCountry);

  return (
    <div>
      <input type="text" placeholder="Buscar país" value={searchTerm} onChange={handleSearch} />
      <nav className="paginadoContainer">
        <ul className="ul">
          {
            pageNumbers && pageNumbers.map(number =>(
              <li key={number}>
                <a href onClick={() => paginated(number)} className="numeroPaginado">{number}</a>
              </li>
            ))
          }
        </ul>
      </nav>
      <ul>
        {currentCountries.map((country) => (
          <li key={country.id}>
            <img src={country.flag} alt={country.name} />
            <div>
              <h2>{country.name}</h2>
              <p>Capital: {country.capital}</p>
              <p>Population: {country.population}</p>
            </div>
          </li>
        ))}
      </ul>
    </div>
  )
}

export default Paginated;
