import React, {useState, useEffect  } from 'react';
import { useDispatch, useSelector} from 'react-redux';
import { searchCountries, setCurrentPage  } from "../redux/actions.js";
import './SearchBar.css';
//import { setCurrentPage} from '../Paginated/Paginated.jsx';

const SearchBar = (props) => {
    const currentPage = []
    const setCurrentPage= []
    const dispatch = useDispatch();
    const paisesNombre = useSelector(state => state.countries.slice((props.currentPage -1) * 10, props.currentPage * 10))
    const [input, setInput] = useState('');

     const handleChange = (event) => {
         setInput(event.target.value)
     }

    const handleSubmit = (event) => {
        event.preventDefault();
        dispatch(searchCountries (input, currentPage + 1));
        setInput('');
    }
       
  const handlePrevPage = () => {
    setCurrentPage(currentPage - 1);
    dispatch(searchCountries(input, currentPage - 1));
  };

    useEffect(() =>{
        dispatch(searchCountries(input, currentPage));
    }, [dispatch, input, currentPage]);
             
    return ( 
        <div>
            <form className='from' onSubmit={ event => handleSubmit(event)}>
                <input 
                    type="text"
                    id="title"
                    autoComplete='off'
                    value={input}
                    placeholder= "Buscar Paises..."
                    onChange={event=>handleChange(event)}
                />
                <button type="submit">Buscar</button>
            </form>
            
            {paisesNombre.map(paisName =>
                <div className="submit-button" key={paisName.name}>
                    <h3>{paisName.name}</h3>
                    <img src={paisName.flag} alt="" />
                    <div>
                        <h5>Capital: {paisName.capital}</h5>
                        <h5>Continent: {paisName.continent}</h5>
                    </div>
                </div>
            )}
        
     <div className="pagination-buttons">
     <button
       type="button"
       disabled={currentPage === 1}
       onClick={handlePrevPage}
     >
       Anterior
     </button>
     <button type="button" onClick={handleNextPage}>
       Siguiente
     </button>
   </div>
 </div>
);
};
    
    

export default SearchBar;
