import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector} from 'react-redux';
import { searchCountries } from '../redux/actions';
import { useHistory } from 'react-router-dom';

const CountryPaisNombre = () => {
  const dispatch = useDispatch();
 const pais = useSelector(state => state.countries);
  const history = useHistory();
  const [input, setInput] = useState('');


const handleChange = (event) => {
    setInput(event.target.value)
}

const handleSubmit = (event) => {
   event.preventDefault();
   dispatch(searchCountries(input));
   setInput('');
}

useEffect(() => { 
  console.log("jose ")
    dispatch(searchCountries(input));
  }, [dispatch, input]);

 return (
    <div>
       <div key={pais.name} onSubmit={ event => handleSubmit(event)}>
            <p>{pais.name} </p>
            <img src={pais.flag ? pais.flag : "no hay banderas"} alt="" />
            <p>id: {pais.id}</p>
            <p>Capital: {pais.capital}</p>
            <p>Continent: {pais.continents}</p>
            <p>SubRegion: {pais.subregion}</p>
            <p>Area: {pais.area}</p>
            <p>Poblation: {pais.population}  onChange={event=>handleChange(event)}</p>
        </div>
     </div>
  );
};

export default CountryPaisNombre;
