import React from 'react'

const Paginated = ({ countriesPerPage, countries, paginated}) => {
    const pageNumbers = [];

    for (let i = 1; i <= Math.ceil(countries/countriesPerPage); i++) {
        pageNumbers.push(i);
    }
    return (
        <div>
            <nav>
                <ul>
                    {
                        pageNumbers && pageNumbers.map(number =>(
                            <li key={number}>
                                <a href onClick={() => paginated(number)}>{number}</a>
                            </li>
                        ))
                    }
                </ul>
            </nav>
            <div>
                {countries.slice((currentPage - 1) * countriesPerPage, currentPage * countriesPerPage).map(country => (
                    <div key={country.alpha3Code}>
                        <h3>{country.name}</h3>
                        <p>{country.capital}</p>
                        <img src={country.flag} alt={country.name} width="100" />
                    </div>
                ))}
            </div>
        </div>
    )
}

export default Paginated
