import React from 'react';
import { useDispatch } from 'react-redux';
import { Link } from "react-router-dom";
import { getCountries } from  "../redux/actions"
import './LandingPage.css';

const LandingPage = () => {
    const dispatch = useDispatch();

    const handleClick = () => {
        dispatch(getCountries());
    }
   return (
        <div className="container">
          <div className='h2Lading'>
            <h2  >Bienvenido a un espacio dedicado a todos los paises del mundo...</h2>
            <h2 >Buscar Países</h2>
            <h2 className='h2Lading' >Ordenar Países por Población y Alfabeto</h2>
            <h2 className='h2Lading' >Filtrar Países por Continente y Actividad Turistica</h2>
            <h2 className='h2Lading' >Crear una Actividad Turistica</h2>
            <h2 className='h2Lading' >Ingresa a la App</h2>
            <Link to='/home' className="link">  
               <button className="button" onClick={handleClick}>Ingresar</button>
            </Link>
          </div>
        </div>
    ) 
};


export default LandingPage;
