import React from 'react';
import { useParams } from 'react-router-dom/cjs/react-router-dom.min';
import { useState, useEffect } from 'react';


const CountryDetail = () => {
  const { input } = useParams()
  
  const [character, setCharacter] = useState( {
    name:"",
    glag:"",
    continent:"",
    capital:"",
    population:"" 
  }); 

 useEffect(() => {
     fetch('http://localhost:3001/countries?name=' + input)
         .then((response) => response.json())
         .then((char) => {
           if (char.name) {
             setCharacter(char);
           } else {
             window.alert("No hay personajes con ese ID");
           }
         })
         .catch((err) => {
           window.alert("No hay personajes con ese ID");
         });
     }, [input]);


  return (
    <div>
       <div>
          <img src={character.flag} alt="Not found" />
          <h1>{character.name}</h1>
          <div>
            <h2>Id: {character.id}</h2>
            <h2>Capital: {character.capital}</h2>
            <h2>Continent: {character.continent}</h2>
            <h2>SubRegion: {character.subregion}</h2>
            <h2>Area: {character.area}</h2>
            <h2>Population: {character.population}</h2>
     
          </div>
          {<div>
            {character.activities?.map((activity) => {
              return (
                <div key={activity.id}>
                  <h2>Activity</h2>
                  <div>
                    <h3>{activity.name}</h3>
                    <h3>Difficulty: {activity.difficulty}</h3>
                    <h3>Duration: {activity.duration}</h3>
                    <h3>Season: {activity.season}</h3>
                  </div>
                </div>
              );
            })}
          </div>} 
        </div>
      
    </div>
  );
};

export default CountryDetail;
