import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {getDetail} from "../redux/actions"


const CountryDetail = () => {
  const dispatch = useDispatch();
  const paisesNombre = useSelector(state => state.countries.slice())
  const [input, setInput] = useState('');

  useEffect(() =>{
      dispatch(getDetail(input));
  }, [dispatch, input]);
           
  return ( 
    <div>
         <div className="submit-button" key={paisesNombre.name}>
          <img src={paisesNombre.flag} alt="" />
          <h1>{paisesNombre.name}</h1>
          <h2>Id: {paisesNombre.id}</h2>
          <h2>Capital: {paisesNombre.capital}</h2>
          <h2>Continent: {paisesNombre.continent}</h2>
          <h2>SubRegion: {paisesNombre.subregion}</h2>
          <h2>Area: {paisName.area}</h2>
          <h2>Population: {paisName.population}</h2>

          <div>
            {paisName.activities?.map((activity) => {
              return (
                <div key={activity.id}>
                  <h2>Activity</h2>
                  <div>
                    <h3>{activity.name}</h3>
                    <h3>Difficulty: {activity.difficulty}</h3>
                    <h3>Duration: {activity.duration}</h3>
                    <h3>Season: {activity.season}</h3>
                  </div>
                </div>
              );
            })}
          </div> 
        </div>
      
    </div>
  )
};
  
export default CountryDetail;
