import React, {useState, useEffect  } from 'react';
import { useDispatch, useSelector} from 'react-redux';
import { getCountries, searchCountries } from "../redux/actions.js";
import './SearchBar.css';
import { Link } from 'react-router-dom';
import CountryPaisNombre from "../CountryNombrePais/CountryNombrePais.jsx"

const SearchBar = ({ onSearch }) => {
    const dispatch = useDispatch();
    const paisesNombre = useSelector(state => state.countries)//recordar pasar slice() para paginado
    const [input, setInput] = useState('');
    const [paisNombre, setpaisNombre] = useState('');
    const [query, setQuery] = useState('');

     const handleChange = (event) => {
         setQuery(event.target.value)
     }

    const handleSubmit = (event) => {
        event.preventDefault();
        dispatch(getCountries(query));
       onSearch(query);
    }
   
    // useEffect(() =>{
    //     dispatch(searchCountries(input));
    // }, [dispatch, input]);

     useEffect(() =>{
         dispatch(searchCountries(paisNombre));
     }, [dispatch, paisNombre]);
           
    return ( 
        <div>
            {/* <CountryPaisNombre/>    */}
            return( 
            <form className='from' onSubmit={ event => handleSubmit(event)}>
              <input className='input'
                    type="text"
                    id="title"
                    autoComplete='off'
                    value={input}
                    placeholder= "Buscar Paises..."
                    onChange={event=>handleChange(event)}
                />
                <button className='button' type="submit">Buscar</button>
            )
            </form>
                {paisesNombre.map(paisName =>
                   <div className="submit-button" key={paisName.name}>
                    <h3> <Link to={`/countries/${paisName.id}`}>{paisName.name}</Link> </h3>       
                   <img src={paisName.flag} alt="" />
                   <div>
                   <h5>Continent: {paisName.continents}</h5>
                   </div>
                </div>
            )} 
  </div>
    )
};

export default SearchBar;
