import Paginated from "../Paginated/Paginated";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { getCountries } from "../redux/actions";
import SearchBar from "../SearchBar/SearchBar";
import "./PaginadoDetalle.css"

const PaginatedDetalle = () => {
  const countries = useSelector((state) => state.countries);
  const dispatch = useDispatch();
  const { idPais } = useParams();

  const countriesPerPage = 10;
  const [currentPage, setCurrentPage] = useState(1);
  const totalCountries = countries.length;
  const totalPages = Math.ceil(totalCountries / countriesPerPage);

  const paginated = Array.from({ length: totalPages }, (_, index) => index + 1);
  const lastCountry = currentPage * countriesPerPage;
  const firstCountry = lastCountry - countriesPerPage;
  const currentCountry = countries.slice(firstCountry, lastCountry);

  const handleClick = (pageNumber) => {
    setCurrentPage(pageNumber);
  };
  
  useEffect(() => {
    dispatch(getCountries());
  }, [dispatch]);
  
  
  return (
    <div>
      <Paginated 
      conso
        countriesPerPage={countriesPerPage}
        totalCountries={totalCountries}
        paginated={paginated}
        currentPage={currentPage}
        setCurrentPage={setCurrentPage}
      />
      <div className="formDetalle">
        {currentCountry.map((country) => {
          return (
            <div className="submit-buttonDetalle"  key={country.name}>
              <h1>{country.name}</h1>
              <img className="submit-buttonDetalle img " src={country.flag ? country.flag : "no hay banderas"} alt="" />
              <h2>Continente: {country.continent}</h2>
            </div>
          );
        })} 
       </div> 
    </div>
  );
};

export default PaginatedDetalle;
