import React, {useState, useEffect  } from 'react';
import { useDispatch, useSelector} from 'react-redux';
import { searchCountries  } from "../redux/actions.js";
import './SearchBar.css';


const SearchBar = () => {
    const dispatch = useDispatch();
    const paisesNombre = useSelector(state => state.countries)
    const [input, setInput] = useState('');

     const handleChange = (event) => {
         setInput(event.target.value)
     }

    const handleSubmit = (event) => {
        event.preventDefault();
        dispatch(searchCountries (input));
        setInput('');
    }
       
        useEffect(() =>{
            dispatch(searchCountries(input));
             }, [dispatch, input]);
             
    return ( 
           <form onSubmit={ event => handleSubmit(event)}>
                <input 
                    type="text"
                    id="title"
                    autoComplete='off'
                    value={input}
                    placeholder= "Buscar Paises..."
                    onChange={event=>handleChange(event)}
                />
            
                  <button type="submit">Search</button>
            </form>
            (paisesNombre.map(paisName=>
                <div className="submit-button">
        
                    <h3>{paisName.name}</h3>
                    <img src={paisName.flag} alt="" />
                    <div>
                        <h5>Capital: {paisName.capital}</h5>
                        <h5>Continent: {paisName.continent}</h5>
                        <h5>Population: {paisName.poblation}</h5>
                   </div>
        </div>
  )))
};

export default SearchBar;
