import { useEffect, useState } from 'react';
import {useParams } from 'react-router-dom/cjs/react-router-dom.min';
//import {getDetail} from "../redux/actions"
import SearchBar from '../SearchBar/SearchBar';

const CountryDetail = () => {
    const { detailId } = useParams()
    
    const [character, setCharacter] = useState( {
        
        name: "",
        flag:"",
        continent:"",
        capital:"",
        population:"",
        area:"",
        sub_region:""
    }); 
       
    useEffect(() => {
        fetch(`http://localhost:3001/countries/${detailId}`)
             .then((response) => response.json())
            .then((char) => {
              if (char.name) {
                setCharacter(char);
              } else {
                window.alert("No hay personajes con ese ID");
              }
            })
            .catch((err) => {
              window.alert("No hay personajes con ese ID");
            });
          return setCharacter({});
        }, [detailId]);

      return (
         <div>
      <SearchBar />
         {character && (
          <div>
            <img src={character.flag} alt="Not found" />
            <h1>{character.name}</h1>
            <div>
              <h2>Id: {character.id}</h2>
              <h2>Capital: {character.capital}</h2>
              <h2>Continent: {character.continent}</h2>
              <h2>SubRegion: {character.sub_region}</h2>
              <h2>Area: {character.area}</h2>
              <h2>Population: {character.population}</h2>
            </div>
            <div>
              {character.activities &&
                character.activities.map(activity => (
                  <div key={activity.id}>
                    <h2>Activity</h2>
                    <div>
                      <h3>{activity.name}</h3>
                      <h3>Difficulty: {activity.difficulty}</h3>
                      <h3>Duration: {activity.duration}</h3>
                      <h3>Season: {activity.season}</h3>
                    </div>
                  </div>
                ))}
            </div>
          </div>
        )}
      </div>
    );
  };
  

export default CountryDetail;
