import { useEffect, useState  } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import Loading from "../Loading/Loading";
import { Link, useParams } from 'react-router-dom/cjs/react-router-dom.min';
import {getDetail} from "../redux/actions"


const CountryDetail = () => {
    const dispatch = useDispatch();
    const { id } = useParams();
    const {details} = useSelector( state => state.details[0]);
    const [input, setInput] = useState('');
    // const [character, setCharacter] = useState( {
    //     name: "",
    //     flag:"",
    //     capital: "",
    //     continent:"",
    //     sub_region:"",
    //     area:"",
    //     population:"",
    // }); 
       
    useEffect(() =>{
        dispatch(getDetail(input));
    }, [dispatch, input]);
   
    return (
        <div>
            {details.length ?
                <div>
                    <div>
                        {details.countries.map((details) => (
                            <div key={details.id}>
                                <Link to={`/country/${details.id}`}>{details.name}</Link>
                              
                            </div>
                        ))}
        </div> 
                        <img src={details.flag} alt="Not found"/>
                        <h1>{details.name}</h1>
                        <div>
                            <h2>Id: {details.id}</h2>
                            <h2>Capital: {details.capital}</h2>
                            <h2>Continent: {details.continent}</h2>
                            <h2>SubRegion: {details.sub_region}</h2>
                            <h2>Area: {details.area}</h2>
                            <h2>Population: {details.population}</h2>
                        </div>
                        <div>
                            {
                                details.activities?.map( activity => {
                                    return (
                                        <div>
                                            <h2>Activity</h2>
                                            <div>
                                                <h3>{activity.name}</h3>
                                                <h3>Difficulty: {activity.difficulty}</h3>
                                                <h3>Duration: {activity.duration}</h3>
                                                <h3>Season: {activity.season}</h3>
                                            </div>
                                        </div>
                                    )
                                })
                            }
                        </div>
                    </div> : <Loading/>
            }
        </div>
    );
};

export default CountryDetail;
