import React, { useEffect } from 'react';
import { useDispatch, useSelector} from 'react-redux';
import {searchCountries} from "../redux/actions"
import { useParams } from 'react-router-dom';
import { useHistory } from 'react-router-dom';

const CountryPaisNombre = () => {
  const dispatch = useDispatch();
  const {input} = useParams();
  const pais = useSelector(state => state.countries);
  const history = useHistory();

  useEffect(() =>{ 
  dispatch(searchCountries(input))
     },[dispatch, input]);
           
  return (
    <div>
         <div  key={pais.name}>
         <p>{pais.name}</p>
         <img src={pais.flag ? pais.flag: "no hay banderas"} alt="" />
          <p>id: {pais.id}</p>
          <p>Capital: {pais.capital}</p>
          <p>Continent: {pais.continent}</p>
          <p>SubRegion: {pais.subregion}</p>
          <p>Area: {pais.area}</p>
          <p>Poblation: {pais.poblation}</p>
          <button onClick={() => history.goBack()}>Regresar</button>
         ) : (
      <p>Pa√≠s no encontrado</p>
     </div>
    </div>
  )
 }
export default CountryPaisNombre;
