    const handleSubmit = (event) => {
        event.preventDefault();
        dispatch(getCountries(input));
        setInput(()=>'');
    }
    return (
        <div className="submit-button">
            <form onSubmit={ event => handleSubmit(event)}>
                <input 
                    type="text"
                    id="title"
                    autoComplete='off'
                    value={input}
