import { useState } from 'react';
import { useDispatch } from 'react-redux';
import { useHistory } from 'react-router-dom';
import {postActivities} from "../redux/actions"


export const useForm = ( initialForm, validateForm ) => {
    const [form, setForm] = useState(initialForm);
    const [errors, setErrors] = useState({});
    const dispatch = useDispatch();
    const history = useHistory();

    const handleChange = (event) => {
        const { name, value } = event.target;

        setForm({
            ...form,
            [name] : value,
        });
    };

    const handleBlur = (event) => {
        handleChange(event);
        setErrors(validateForm(form));
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        setErrors(validateForm(form));

        if (Object.keys(errors).length === 0) {
            dispatch(postActivities(form));
            alert('Activity created');
            setForm(initialForm);
            history.push("/home");
        } else {
            return;
        }
    };

      const handleSelect = (event) => {
        const selectedOptions = Array.from(event.target.selectedOptions);
        const selectedCountries = selectedOptions.map((option) => {
            const countryName = option.text;
            const countryAbbr = Name.slice(0, 3);
            return countryAbbr;
        });
        return selectedCountries.join(", ");
    }

    return {
        form,
        errors,
        handleChange,
        handleBlur,
        handleSubmit,
        handleSelect
    }
}
