import React from 'react';
import { useForm } from './useForm';
import NavBar from '../NavBar/NavBar';
import { useSelector } from 'react-redux';
import {AUTUMN, SPRING, SUMMER, WINTER } from "../redux/action-types"
import './ActivityCreate.css';


const initialForm = {
    name: "",
    duration:  { hours: 0, minutes: 0, seconds: 0 },
    difficulty: "",
    season: "",
    id: "",
}

const validationsForm = (form) => {
    let errors = {};
    if (!form.name) {
      errors.name = "Debes completar el campo 'Nombre'.";
    } else if (!form.duration.hours && !form.duration.minutes && !form.duration.seconds) {
      errors.duration = "Debes completar el campo 'Duración'.";
    } else if (!form.difficulty) {
      errors.difficulty = "Debes completar el campo 'Dificultad'.";
    } else if (!form.season) {
      errors.season = "Debes completar el campo 'Temporada'.";
    } else if (!form.id) {
      errors.id = "Debes completar el campo 'Id'.";
    }
    return errors;
  };
const ActivityCreate = () => {
    const countries = useSelector( state => state.countries);
      const {
        form,
        errors,
        handleChange,
        handleSubmit,
        handleSelect
    } = useForm(initialForm, validationsForm)
   console.log("Accion 1 crear actividad")

   const handleDurationChange = (event) => {
    const [hours, minutes, seconds] = event.target.value.split(":").map(Number);
    setForm((prevForm) => ({
      ...prevForm,
      duration: { hours, minutes, seconds },
    }));
  };
  

    return (
        <div>
            <div>
                <NavBar />
            </div>
          <div>
                <div>
                   <form className='form' onSubmit={handleSubmit}>
                        <span>Crear Actividad </span>
                        <div >
                            <label>Nombre: </label>
                            <input
                                type="text"
                                placeholder="Nombre de la actividad..."
                                value={form.name}
                                name="name"
                                onChange={handleChange}
                            />
                            {errors.name && <p>{errors.name}</p>}
                        </div>
                        <div>
                            <label>Duración: </label>
                            <input
                                type="time"
                                value={`${form.duration.hours.toString().padStart(2, "0")}:${form.duration.minutes
                                    .toString()
                                    .padStart(2, "0")}:${form.duration.seconds.toString().padStart(2, "0")}`}
                                name="duration"
                                onChange={handleDurationChange}
                            />
                            {errors.duration && <p>{errors.duration}</p>}
                        </div>

                        <div>
                            <label>Difficultad: </label>
                            <input
                                type="range"
                                name="difficulty"
                                min="1"
                                max="5"
                                value={form.difficulty}
                                onChange={(event) => handleChange(event)}
                            />
                            {errors.difficulty && <p className="e"> {errors.difficulty}</p>}
                        </div>
                        <div >
                            <select
                                name="season"
                                value={form.season}
                                onChange={(event) => handleChange(event)}
                            >
                                <option >Temporadas: </option>
                                <option  value={WINTER}>Winter</option>
                                <option  value={SUMMER}>Summer</option>
                                <option  value={AUTUMN}>Autumn</option>
                                <option  value={SPRING}>Spring</option>
                            </select>
                            {errors.season && <p>{errors.season}</p>}
                        </div>
                        {errors.id && <p>{errors.id}</p>}

                        <div>
                            <select onChange={(event) => handleSelect(event)}>
                                <option>Countries </option>
                                {
                                    countries.map((v) => (
                                        <option  value={v.id}>{v.name}</option>))
                                }
                            </select>
                        </div>
                      <div>
                            <button  type="submit">Crear Actividad</button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default ActivityCreate;
