import React, { useState, useEffect } from 'react';
import { useDispatch} from 'react-redux';
import {searchCountries} from "../redux/actions"
import { useParams } from 'react-router-dom';
import SearchBar from '../SearchBar/SearchBar';

const CountryDetail = () => {
  const dispatch = useDispatch();
  const {paisName} = useParams();
  const [pais, setPais] = useState({});

  useEffect(() =>{ 
  dispatch(searchCountries(paisName)).then(response =>{
      console.log(response, "detalle de los paises")
      return setPais(response.playload)
    })
     }, [dispatch, paisName]);
    console.log(pais, "detalle de los paises")
           
  return (
    <div>
    <SearchBar />
         <div className="submit-button" key={pais.name}>
          <img src={pais.flag} alt="" />
          <p>{pais.name}</p>
          <p>Id: {pais.id}</p>
          <p>Capital: {pais.capital}</p>
          <p>Continent: {pais.continent}</p>
          <p>SubRegion: {pais.subregion}</p>
          <p>Area: {pais.area}</p>
          <p>Population: {pais.population}</p>
          </div>
          </div>
  )
 }
