import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {getDetail} from "../redux/actions"
import { useParams } from 'react-router-dom';



const CountryDetail = () => {
  const dispatch = useDispatch();
  const {paisName} = useParams();
  const [pais, setPais] = useState({});

  useEffect(() =>{ 
  dispatch(getDetail(paisName)).then(response =>{
      console.log(response, "detalle de los paises")
      return setPais(response.playload)
    })
     }, [dispatch, paisName]);
    console.log(pais, "detalle de los paises")
           
  return ( 
    <div>
         <div className="submit-button" key={paisName.name}>
          <img src={paisName.flag} alt="" />
          <h1>{paisName.name}</h1>
          <h2>Id: {paisName.id}</h2>
          <h2>Capital: {paisName.capital}</h2>
          <h2>Continent: {paisName.continent}</h2>
          <h2>SubRegion: {paisName.subregion}</h2>
          <h2>Area: {paisName.area}</h2>
          <h2>Population: {paisName.population}</h2>

          {/* <div>
            {paisName.activities?.map((activity) => {
              return (
                <div key={activity.id}>
                  <h2>Activity</h2>
                  <div>
                    <h3>{activity.name}</h3>
                    <h3>Difficulty: {activity.difficulty}</h3>
                    <h3>Duration: {activity.duration}</h3>
                    <h3>Season: {activity.season}</h3>
                  </div>
                </div>
              );
            })}
          </div>  */}
        </div>
      ))}
    </div>
  )
};
  
export default CountryDetail;
