import React, { useEffect } from 'react';
import { useDispatch, useSelector} from 'react-redux';
import {getDetail, getActivities} from "../redux/actions"
import { useParams } from 'react-router-dom';
import "./countryDetail.css"
import { useHistory } from 'react-router-dom';

const CountryDetail = () => {
  const dispatch = useDispatch();
  const {idPais} = useParams();
  const pais = useSelector(state => state.details);
  const history = useHistory();
  const actividad = useSelector(state => state.activities);

  useEffect(() =>{ 
  dispatch(getDetail(idPais))
  dispatch(getActivities(actividad))
     },[dispatch, idPais]);
           
  return (
    <div>
         <div className="submit-button1" key={pais.name}>
         <p>{pais.name}</p>
         <img src={pais.flag ? pais.flag: "no hay banderas"} alt="" />
          <p>Id: {pais.id}</p>
          <p>Capital: {pais.capital}</p>
          <p>Continent: {pais.continent}</p>
          <p>SubRegion: {pais.subregion}</p>
          <p>Area: {pais.area}</p>
          <p>Poblation: {pais.population}</p>
          <br />
          {actividad.loading ? (
               <p>Cargando actividad...</p>
           ) : (
          <>
          {actividad.activities.map((act) => (
          <div key={act.id}>
          <p>Nombre: {act.name}</p>
          <p>Dificultad:{act.difficulty}</p>
          <p>Duracion:{act.duration}</p>
          <p>Season:{act.season}</p>
          <p>CountryId:{act.countryId}</p>
      </div>
    ))}
  </>
)}
    <button onClick={() => history.goBack()}>Regresar</button>
     </div>
    </div>
  )
 }
export default CountryDetail;
