import React, { useEffect  } from 'react';
import { useDispatch, useSelector} from 'react-redux';
import { searchCountries  } from "../redux/actions.js";
import './SearchBar.css';



const SearchBar = () => {
    const dispatch = useDispatch();
    const paisesNombre = useSelector(state => state.countries)
    const input = useSelector(state => state.input);

    
     const handleChange = (event) => {
         setInput(()=> event.target.value)
     }

    const handleSubmit = (event) => {
        event.preventDefault();
        dispatch(searchCountries (input));
        setInput('');
    }
       
        useEffect(() =>{
            dispatch(searchCountries(input));
             }, [dispatch, input]);
             
    return ( paisesNombre.map(paisName=>
        <div className="submit-button">

            <h3>{paisName.name}</h3>
            <img src={paisName.flag} alt="" />
            <div>
                <h5>Capital: {paisName.capital}</h5>
                <h5>Continent: {paisName.continent}</h5>
                <h5>Population: {paisName.poblation}</h5>

            </div>


            <form onSubmit={ event => handleSubmit(event)}>
                <input 
                    type="text"
                    id="title"
                    autoComplete='off'
                    value={input}
                    placeholder= "Buscar Paises..."
                    onChange={event=>handleChange(event)}
                />
            
                  <button type="submit">Search</button>
            </form>
        </div>
  ))
};

export default SearchBar;
