import { useParams, useState } from 'react-router-dom';
import { getDetail } from '../redux/actions';
import SearchBar from '../SearchBar/SearchBar';

const CountryDetail = () => {
  const { details } = useParams()
  
  const [character, setCharacter] = useState( {
    id:"",
    name: "",
    flag: "",
    continent:"",
    capital:"",
    population:"",
    area:"",
    sub_region:""
}); 

useEffect(() => {
    fetch(`http://localhost:3001/countries/' + id`)
        .then((response) => response.json())
        .then((char) => {
          if (!char.name) {
            setCharacter(char);
          } else {
            window.alert("No hay personajes con ese ID");
          }
        })
        .catch((err) => {
          window.alert("No hay personajes con ese ID");
        });
      return setCharacter({});
    }, [details]);


  return (
    <div>
     <SearchBar />
      {details && (
        <div>
          <img src={details.flag} alt="Not found" />
          <h1>{details.name}</h1>
          <div>
            <h2>Id: {details.id}</h2>
            <h2>Capital: {details.capital}</h2>
            <h2>Continent: {details.continent}</h2>
            <h2>SubRegion: {details.sub_region}</h2>
            <h2>Area: {details.area}</h2>
            <h2>Population: {details.population}</h2>
          </div>
          {/* <div>
            {details.activities?.map((activity) => {
              return (
                <div key={activity.id}>
                  <h2>Activity</h2>
                  <div>
                    <h3>{activity.name}</h3>
                    <h3>Difficulty: {activity.difficulty}</h3>
                    <h3>Duration: {activity.duration}</h3>
                    <h3>Season: {activity.season}</h3>
                  </div>
                </div>
              );
            })}
          </div> */}
        </div>
      )}
    </div>
  );
};

export default CountryDetail;
