import { useEffect, useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { Link, useParams } from 'react-router-dom';
import { getDetail } from '../redux/actions';
import SearchBar from '../SearchBar/SearchBar';

const CountryDetail = () => {
  const dispatch = useDispatch();
  const { id } = useParams();
  const { details } = useSelector((state) => state.details);
  const [input, setInput] = useState('');

  useEffect(() => {
    dispatch(getDetail(id));
  }, [dispatch, id]);

  return (
    <div>
      {details && (
        <div>
          <div>
            {details.countries.map((country) => (
              <div key={country.id}>
                <Link to={`/country/${country.id}`}>{country.name}</Link>
              </div>
            ))}
          </div>
          <img src={details.flag} alt="Not found" />
          <h1>{details.name}</h1>
          <div>
            <h2>Id: {details.id}</h2>
            <h2>Capital: {details.capital}</h2>
            <h2>Continent: {details.continent}</h2>
            <h2>SubRegion: {details.sub_region}</h2>
            <h2>Area: {details.area}</h2>
            <h2>Population: {details.population}</h2>
          </div>
          <div>
            {details.activities?.map((activity) => {
              return (
                <div key={activity.id}>
                  <h2>Activity</h2>
                  <div>
                    <h3>{activity.name}</h3>
                    <h3>Difficulty: {activity.difficulty}</h3>
                    <h3>Duration: {activity.duration}</h3>
                    <h3>Season: {activity.season}</h3>
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      )}
    </div>
  );
};

export default CountryDetail;
