import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector} from 'react-redux';
import {getDetail} from "../redux/actions"
import { useParams } from 'react-router-dom';

const CountryDetail = () => {
  const dispatch = useDispatch();
  const {paisName} = useParams();
  const pais = useSelector(state => state.detail);
  console.log(pais)

  useEffect(() =>{ 
  dispatch(getDetail(paisName))
     }, [paisName]);
           
  return (
    <div>
         <div className="submit-button" key={pais.name}>
          <img src={pais.flags} alt="" />
          <p>{pais.name}</p>
          <p>Id: {pais.id}</p>
          <p>Capital: {pais.capital}</p>
          <p>Continent: {pais.continent}</p>
          <p>SubRegion: {pais.subregion}</p>
          <p>Area: {pais.area}</p>
          <p>Population: {pais.population}</p>
          </div>
          </div>
  )
 }
           {/* <div>
            {paisName.activities?.map((activity) => {
              return (
                <div key={activity.id}>
                  <h2>Activity</h2>
                  <div>
                    <h3>{activity.name}</h3>
                    <h3>Difficulty: {activity.difficulty}</h3>
                    <h3>Duration: {activity.duration}</h3>
                    <h3>Season: {activity.season}</h3>
                  </div>
                </div>
              );
            })}
          </div>  
        </div>
      ))}  
    </div>
  )
};
};*/}



export default CountryDetail;
